<?include_once("include/config.php");include_once("include/css/default.css");include_once("include/load_xml.php");$base_url="http://";if ($HTTPS==1){$base_url="https://";}$base_url.=$_SERVER["SERVER_NAME"]."/".$INSTALL_HOME;// To display systems not audited?><table class="reporttable" width=100% cellspacing=1 cellpadding=2><tr>	<td class='reportdata' style='color:red' width=100%><b>Systems not Audited in Last <?echo $AUDIT_EXPIRY;?> days</td></tr></table><table class="reporttable" width=100% cellspacing=1 cellpadding=2><tr>	<td class="reportheader">Asset Tag</td>	<td class="reportheader">Hostname</td>		<td class="reportheader">Assigned To</td>		<td class="reportheader">Last Audited</td></td></tr><?// Generate current unix time stamp$today=mktime(23,59,0,date('m'),date('d'),date('Y'));$url=$base_url."/api/ast_list_active_hosts.php?key=$API_KEY";$slno=0;if ($query = load_xml($url)){	for($i=0;$i<count($query);$i++){				$tag=$query->asset[$i]->tag;		$hostname=$query->asset[$i]->hostname;		$assignedto=$query->asset[$i]->assignedto;				$sub_url_1=$base_url."/api/oa_audit_information.php?key=$API_KEY&hostname=$hostname";		$sub_query_1 = load_xml($sub_url_1);				$last_audited=$sub_query_1->host[0]->last_audited;		if (strlen($last_audited)>0){			$last_audited=$last_audited*1;			$print_date=date('d M Y',$last_audited);					}else{			$print_date="Never Audited";		}		$diff=($today-$last_audited)/86400;		$diff=round($diff,0);						if ($diff>$AUDIT_EXPIRY){ 			$slno=$slno+1;						echo "<tr bgcolor=#F0F0F0>";			if (strlen($tag)==1){$id="0000".$tag;}			if (strlen($tag)==2){$id="000".$tag;}			if (strlen($tag)==3){$id="00".$tag;}			if (strlen($tag)==4){$id="0".$tag;}			if (strlen($tag)==5){$id="".$tag;}						echo "<td class='reportdata' width=70 ><a href=ezasset/edit_asset_1.php?assetid=$tag>$ASSET_PREFIX-$id</a></td>";			echo "<td class='reportdata'> $hostname</td>";			echo "<td class='reportdata'> $assignedto</td>";									echo "<td class='reportdata' style='text-align: center;'>$print_date</td>";		}		}}echo "<tr><td class=reportdata colspan=4 style='text-align: left;'>TOTAL : $slno</td></tr>";echo "</table>";// End of software compliance summary?>