<?phpinclude("../../auth.php");$status=$_REQUEST['status'];if($_REQUEST["xls"]==1){	header("Content-Type: application/vnd.ms-excel");	header("Content-disposition: attachment; filename=Tickets.xls\n"); }$sql="select * from isost_ticket where status = '$status' order by ticket_id desc";$result=mysql_query($sql);$num_rows=mysql_num_rows($result);if($num_rows > 0){	while($row=mysql_fetch_array($result))	{		$priorityid=$row['priority_id'];		$staffid=$row['staff_id'];		$tickettypeid=$row['ticket_type_id'];		$ticketid[]=$row['ticket_id'];		$raisedby[]=$row['name'];		$subject[]=$row['subject'];		$helptopic[]=$row['helptopic'];		$date[]=$row['created'];				$pri_sql=mysql_query("select * from isost_ticket_priority where priority_id = '$priorityid'");		$row_pri=mysql_fetch_array($pri_sql);		$priority[]=$row_pri['priority_desc'];		$prioritycolor[]=$row_pri['priority_color'];				$ttype_sql=mysql_query("select * from isost_ticket_type where ticket_type_id = '$tickettypeid'");		$row_ttype=mysql_fetch_array($ttype_sql);		$tickettype[]=$row_ttype['ticket_type'];				if($staffid > 0)		{			$staff_sql=mysql_query("select * from isost_staff where staff_id = '$staffid'");			$row_staff=mysql_fetch_array($staff_sql);			$staffname[]=$row_staff['firstname'].' '.$row_staff['lastname'];		}		else		{			$staffname[]='-- unassigned --';		}	} ?>		<table width='100%' border='0' cellspacing=0 cellpadding=2 class='dtable' align='center'>	<tr>           		<th width="72" style="background-color:#ADADAD; color:#ffffff; text-align:left;">&nbsp;</th>		<th width="80" style="background-color:#ADADAD; color:#ffffff; text-align:left;">&nbsp;</th>		<th width="100" style="background-color:#ADADAD; color:#ffffff; text-align:left;">&nbsp;</th>		<th width="258" style="background-color:#ADADAD; color:#004452; text-align:right; font-size:20px;">Report of <?=$status?> tickets</th>		<th width="203" style="background-color:#ADADAD; color:#ffffff; text-align:left;">&nbsp;</th>		<th width="79" style="background-color:#ADADAD; color:#ffffff; text-align:left;">&nbsp;</th>		<th width="136" style="background-color:#ADADAD; color:#ffffff; text-align:left;">&nbsp;</th>		<th width="136" style="background-color:#ADADAD; color:#ffffff; text-align:left;">&nbsp;</th>    </tr>	<tr>	   		<th width="72" style="background-color:#ADADAD; color:#ffffff; text-align:left;">Ticket</th>		<th width="80" style="background-color:#ADADAD; color:#ffffff; text-align:left;">Date</th>		<th width="80" style="background-color:#ADADAD; color:#ffffff; text-align:left;">Ticket Type</th>		<th width="258" style="background-color:#ADADAD; color:#ffffff; text-align:left;">Subject</th>		<th width="203" style="background-color:#ADADAD; color:#ffffff; text-align:left;">Help Topic</th>		<th width="79" style="background-color:#ADADAD; color:#ffffff; text-align:left;">Priority</th>		<th width="136" style="background-color:#ADADAD; color:#ffffff; text-align:left;">From</th>		<th width="136" style="background-color:#ADADAD; color:#ffffff; text-align:left;">Assigned To</th>	</tr>			<?php	$j=1;	for($i=0; $i<count($priority); $i++)	{		$createddate=explode(' ',$date[$i]);		$cdate=date('d-m-Y',strtotime($createddate[0]));				if($j%2 != 0)		{	?>		<tr ><td style="background-color:#F4FAFF; color:#000000; text-align:left;"><?php print $ticketid[$i]; ?></td><td style="background-color:#F4FAFF; color:#000000; text-align:left;"><?php print $cdate; ?></td><td style="background-color:#F4FAFF; color:#000000; text-align:left;"><?php print $tickettype[$i]; ?></td><td style="background-color:#F4FAFF; color:#000000; text-align:left;"><?php print $subject[$i]; ?></td><td style="background-color:#F4FAFF; color:#000000; text-align:left;"><?php print $helptopic[$i]; ?></td><td style="background-color:<?=$prioritycolor[$i]?>; color:#000000; text-align:left;"><?php print $priority[$i]; ?></td><td style="background-color:#F4FAFF; color:#000000; text-align:left;"><?php print $raisedby[$i]; ?></td><td style="background-color:#F4FAFF; color:#000000; text-align:left;"><?php print $staffname[$i]; ?></td></tr>	<?php		} 		else		{ 	?>		<tr ><td style="background-color:#FFFFFF; color:#000000; text-align:left;"><?php print $ticketid[$i]; ?></td><td style="background-color:#FFFFFF; color:#000000; text-align:left;"><?php print $cdate; ?></td><td style="background-color:#FFFFFF; color:#000000; text-align:left;"><?php print $tickettype[$i]; ?></td><td style="background-color:#FFFFFF; color:#000000; text-align:left;"><?php print $subject[$i]; ?></td><td style="background-color:#FFFFFF; color:#000000; text-align:left;"><?php print $helptopic[$i]; ?></td><td style="background-color:<?=$prioritycolor[$i]?>; color:#000000; text-align:left;"><?php print $priority[$i]; ?></td><td style="background-color:#FFFFFF; color:#000000; text-align:left;"><?php print $raisedby[$i]; ?></td><td style="background-color:#FFFFFF; color:#000000; text-align:left;"><?php print $staffname[$i]; ?></td></tr>	<?php 		}	$j++; 	} 	?></table><?php}?>