<?	// Fetch Master Configuration		$sql	="select * from config";	$result = mysql_query($sql);	$row 	= mysql_fetch_row($result);			$ACCOUNT_AS_EMAIL=$row[0];			// Enable MD5 Storage of Passwords	$MD5_ENABLE=$row[1];			// Prefix that will be attached to Asset Tags	$ASSET_PREFIX=$row[2];			// Number of Days after which Audit Information for an asset expires	$AUDIT_EXPIRY=$row[3];			// Enable only if ezRIM Management Console is being used.	// If enabled, provide the details below for $MASTER_URL, $MASTER_API_KEY, $AGENCY_ID	$ezRIM		=$row[4];			// Specify the URL of the Master RIM Instance being used	$MASTER_URL	=$row[5];			// Specify the API KEY of the Master RIM Instance being used	$MASTER_API_KEY=$row[6];			// Specify the AGENCY_INDEX where this instance of the RIM BOX is installed	$AGENCY_ID=$row[7];			$DASH		=$row[8];	$EZASSET	=$row[9];	$EZTICKET	=$row[10];	$NETWORK	=$row[11];	$HTTPS		=$row[12];	$SNMP       =$row[13];	$TERMINAL	=$row[14];	$TERMINALPORT   =$row[15];	// End of Master configuration?>